"use strict";(self.webpackChunkeffective_shell=self.webpackChunkeffective_shell||[]).push([[53],{1109:e=>{e.exports=JSON.parse('{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"sidebar":[{"type":"link","label":"Introduction","href":"/","docId":"index"},{"type":"category","label":"Transitioning to the Shell","items":[{"type":"link","label":"Getting Started","href":"/part-1-transitioning-to-the-shell/getting-started/","docId":"transitioning-to-the-shell/getting-started/index"},{"type":"link","label":"Navigating Your System","href":"/part-1-transitioning-to-the-shell/navigating-your-system/","docId":"transitioning-to-the-shell/navigating-your-system/index"},{"type":"link","label":"Managing Your Files","href":"/part-1-transitioning-to-the-shell/managing-your-files/","docId":"transitioning-to-the-shell/managing-your-files/index"},{"type":"link","label":"Becoming a Clipboard Gymnast","href":"/part-1-transitioning-to-the-shell/become-a-clipboard-gymnast/","docId":"transitioning-to-the-shell/clipboard-gymnastics/index"},{"type":"link","label":"Getting Help","href":"/part-1-transitioning-to-the-shell/getting-help/","docId":"transitioning-to-the-shell/getting-help/index"},{"type":"link","label":"The Renaissance of the Shell","href":"/part-1-transitioning-to-the-shell/the-renaissance-of-the-shell/","docId":"transitioning-to-the-shell/the-renaissance-of-the-shell/index"}],"collapsed":true,"collapsible":true,"href":"/part-1-transitioning-to-the-shell/"},{"type":"category","label":"Core Skills","items":[{"type":"link","label":"Thinking in Pipelines","href":"/part-2-core-skills/thinking-in-pipelines/","docId":"core-skills/thinking-in-pipelines/index"},{"type":"link","label":"Fly on the Command Line","href":"/part-2-core-skills/fly-on-the-command-line","docId":"core-skills/fly-on-the-command-line/index"},{"type":"link","label":"Job Control","href":"/part-2-core-skills/job-control","docId":"core-skills/job-control/index"},{"type":"link","label":"Understanding Commands","href":"/part-2-core-skills/understanding-commands","docId":"core-skills/understanding-commands/index"},{"type":"link","label":"Finding Files","href":"/part-2-core-skills/finding-files","docId":"core-skills/finding-files/index"},{"type":"link","label":"What is a Shell?","href":"/part-2-core-skills/what-is-a-shell","docId":"core-skills/what-is-a-shell/index"}],"collapsed":true,"collapsible":true,"href":"/part-2-core-skill/"},{"type":"category","label":"Manipulating Text and Streams","items":[{"type":"link","label":"Regex Essentials","href":"/part-3-manipulating-text/regex-essentials/","docId":"manipulating-text/regex-essentials/index"},{"type":"link","label":"Get to Grips with Grep","href":"/part-3-manipulating-text/get-to-grips-with-grep/","docId":"manipulating-text/get-to-grips-with-grep/index"},{"type":"link","label":"Slice and Dice Text","href":"/part-3-manipulating-text/slice-and-dice-text/","docId":"manipulating-text/slice-and-dice-text/index"},{"type":"link","label":"Advanced Text Manipulation","href":"/part-3-manipulating-text/advanced-text-manipulation/","docId":"manipulating-text/advanced-text-manipulation/index"},{"type":"link","label":"Build Commands on the Fly","href":"/part-3-manipulating-text/build-commands-on-the-fly/","docId":"manipulating-text/build-commands-on-the-fly/index"}],"collapsed":true,"collapsible":true,"href":"/part-3-manipulating-text/"},{"type":"category","label":"Shell Scripting","items":[{"type":"link","label":"Shell Script Essentials","href":"/part-3-manipulating-text/shell-script-essentials/","docId":"shell-scripting/shell-script-essentials/index"},{"type":"link","label":"Variables, Reading Input, and Mathematics","href":"/part-3-manipulating-text/variables-reading-input-and-mathematics/","docId":"shell-scripting/variables-reading-input-and-mathematics/index"},{"type":"link","label":"Mastering Conditional Logic","href":"/part-4-shell-scripting/mastering-conditional-logic","docId":"shell-scripting/mastering-conditional-logic/index"},{"type":"link","label":"Loops and working with Files and Folders","href":"/part-4-shell-scripting/loops-and-working-with-files-and-folders","docId":"shell-scripting/loops-and-working-with-files-and-folders/index"},{"type":"link","label":"Functions, Parameters and Error Handling","href":"/part-4-shell-scripting/functions-parameters-and-error-handling","docId":"shell-scripting/functions-parameters-and-error-handling/index"},{"type":"link","label":"Useful Patterns for Shell Scripts","href":"/part-4-shell-scripting/useful-patterns-for-shell-scripts","docId":"shell-scripting/useful-patterns-for-shell-scripts/index"}],"collapsed":true,"collapsible":true,"href":"/part-4-shell-scripting/"},{"type":"category","label":"Building Your Toolkit","items":[{"type":"link","label":"Configuring the Shell","href":"/part-5-building-your-toolkit/configuring-the-shell","docId":"building-your-toolkit/configuring-the-shell/index"},{"type":"link","label":"Customising Your Command Prompt","href":"/part-5-building-your-toolkit/customising-your-command-prompt","docId":"building-your-toolkit/customising-your-command-prompt/index"},{"type":"link","label":"Managing your Dotfiles","href":"/part-5-building-your-toolkit/managing-your-dotfiles","docId":"building-your-toolkit/managing-your-dotfiles/index"},{"type":"link","label":"Controlling Changes with Git","href":"/part-5-building-your-toolkit/controlling-changes-with-git","docId":"building-your-toolkit/controlling-changes-with-git/index"},{"type":"link","label":"Managing Remote Git Repositories and Sharing Your Dotfiles","href":"/part-5-building-your-toolkit/managing-rempte-git-repositories/","docId":"building-your-toolkit/managing-remote-git-repositories/index"}],"collapsed":true,"collapsible":true,"href":"/part-5-building-your-toolkit/"},{"type":"category","label":"Advanced Techniques","items":[{"type":"link","label":"Understanding Shell Expansion","href":"/part-6-advanced-techniques/understanding-shell-expansion/","docId":"advanced-techniques/understanding-shell-expansion/index"},{"type":"link","label":"How to Avoid Scripting - A Dictionary Lookup in Python","href":"/part-6-advanced-techniques/how-to-avoid-scripting/","docId":"advanced-techniques/how-to-avoid-scripting/index"},{"type":"link","label":"The Secure Shell","href":"/part-6-advanced-techniques/the-secure-shell/","docId":"advanced-techniques/the-secure-shell/index"},{"type":"link","label":"A Vim Crash Course","href":"/part-6-advanced-techniques/a-vim-crash-course/","docId":"advanced-techniques/a-vim-crash-course/index"},{"type":"link","label":"Master the Multiplexer","href":"/part-6-advanced-techniques/master-the-multiplexer/","docId":"advanced-techniques/master-the-multiplexer/index"}],"collapsed":true,"collapsible":true,"href":"/part-6-advanced-techniques/"},{"type":"category","label":"Appendices","items":[{"type":"link","label":"Installing the Samples","href":"/xx-appendices/installing-samples/","docId":"xx-appendices/installing-samples/index"},{"type":"link","label":"Recommended Reading","href":"/xx-appendices/recommended-reading/","docId":"xx-appendices/recommended-reading/index"},{"type":"link","label":"Thanks","href":"/appendices/thanks/","docId":"xx-appendices/thanks"}],"collapsed":true,"collapsible":true},{"type":"category","label":"Developer Guide","items":[{"type":"link","label":"Components","href":"/zz-developer-guide/components","docId":"zz-developer-guide/components"},{"type":"link","label":"Images and Diagrams","href":"/zz-developer-guide/images-and-diagrams","docId":"zz-developer-guide/images-and-diagrams"},{"type":"link","label":"Markdown","href":"/zz-developer-guide/markdown-guide","docId":"zz-developer-guide/markdown-guide"},{"type":"link","label":"Recording Terminal Sessions","href":"/zz-developer-guide/recording-terminal-sessions","docId":"zz-developer-guide/recording-terminal-sessions"}],"collapsed":true,"collapsible":true}]},"docs":{"advanced-techniques/a-vim-crash-course/index":{"id":"advanced-techniques/a-vim-crash-course/index","title":"A Vim Crash Course","description":"In this chapter we\'re going to introduce the popular Terminal Editor Vim. A Terminal Editor is a text editor that you can open directly in your terminal, normally from the shell. Many users find these editors hard to use, and some of them have a reputation for being highly complex.","sidebar":"sidebar"},"advanced-techniques/how-to-avoid-scripting/index":{"id":"advanced-techniques/how-to-avoid-scripting/index","title":"How to Avoid Scripting - A Dictionary Lookup in Python","description":"This book is about being an effective shell user. This is not a book about shell scripting. And sometimes being an effective shell user means knowing when to not use a shell script to solve a problem, but to use an alternative tool such as a programming language.","sidebar":"sidebar"},"advanced-techniques/index":{"id":"advanced-techniques/index","title":"Part 6 - Advanced Techniques","description":"At this stage you are well on your way to shell mastery. Each of the chapters in this section goes introduces either an advanced technique to help you be more effective in the shell, or goes considerably deeper into one of the topics that we have already covered so that you can really understand the fundamentals.","sidebar":"sidebar"},"advanced-techniques/master-the-multiplexer/index":{"id":"advanced-techniques/master-the-multiplexer/index","title":"Master the Multiplexer","description":"If you are regularly using a shell, then learning how to use a terminal multiplexer like screen or tmux can greatly improve your productivity. In this chapter we\'ll see how what a terminal multiplexer is, what it is used for, learn how to perform some common tasks and configure a multiplexer to make it even more useful.","sidebar":"sidebar"},"advanced-techniques/the-secure-shell/index":{"id":"advanced-techniques/the-secure-shell/index","title":"The Secure Shell","description":"So far we have been using the shell to operate on our local machine. We can use Secure Shell Protocol, or SSH, to open a secure network connection to a remote machine and use the shell to work on that machine.","sidebar":"sidebar"},"advanced-techniques/understanding-shell-expansion/index":{"id":"advanced-techniques/understanding-shell-expansion/index","title":"Understanding Shell Expansion","description":"When you are working with the shell there are a number of techniques that you can use to take simple commands and make more useful. For example, if we wanted to create three files, we could run touch file1 file2 file3, or we could use \'brace expansion\' and just run touch file{1..3}. Another example would be to delete all files that have names that start with file - like this rm file*, this is wildcard expansion.","sidebar":"sidebar"},"building-your-toolkit/configuring-the-shell/index":{"id":"building-your-toolkit/configuring-the-shell/index","title":"Configuring the Shell","description":"There are a number of different ways to configure your shell. In this chapter we\'ll take a look at the different configuration files for the shell and how they work, and how you can change your shell configuration with options.","sidebar":"sidebar"},"building-your-toolkit/controlling-changes-with-git/index":{"id":"building-your-toolkit/controlling-changes-with-git/index","title":"Controlling Changes with Git","description":"Git is an extremely popular version control tool. You can use Git to track changes to text, code, or any type of files you might be working with. Many popular projects use Git as a tool to manage changes, allow others to contribute and collaborate, and to publish their projects.","sidebar":"sidebar"},"building-your-toolkit/customising-your-command-prompt/index":{"id":"building-your-toolkit/customising-your-command-prompt/index","title":"Customising Your Command Prompt","description":"The shell has a large number of options available that you can use to customise the command prompt - the text shown in front of your cursor as you type commands. In this chapter we will look at how you can change the command prompt to show the information that you would like to see.","sidebar":"sidebar"},"building-your-toolkit/index":{"id":"building-your-toolkit/index","title":"Part 5 - Building Your Toolkit","description":"We\'ve now seen many of the core features and some of the more advanced capabilities of the shell. In this part of the book are are going to look at how the shell is configured, different ways a shell can run, and effective ways to manage your shell configuration.","sidebar":"sidebar"},"building-your-toolkit/managing-remote-git-repositories/index":{"id":"building-your-toolkit/managing-remote-git-repositories/index","title":"Managing Remote Git Repositories and Sharing Your Dotfiles","description":"In this chapter we\'ll take a look at how to take a local Git repository, like the one created in the previous chapter, and upload it to a remote repository.","sidebar":"sidebar"},"building-your-toolkit/managing-your-dotfiles/index":{"id":"building-your-toolkit/managing-your-dotfiles/index","title":"Managing your Dotfiles","description":"As you build up more and more customisations for your shell and environment, it becomes important to find a way to manage these changes and files effectively.","sidebar":"sidebar"},"core-skills/finding-files/index":{"id":"core-skills/finding-files/index","title":"Finding Files","description":"Searching through a system to find files or folders can be complex and time consuming, even with a graphical user interface. In this chapter we\'ll look at how to use the shell to search for files and folders and some quick ways to accomplish common tasks.","sidebar":"sidebar"},"core-skills/fly-on-the-command-line/index":{"id":"core-skills/fly-on-the-command-line/index","title":"Fly on the Command Line","description":"This is my favourite chapter of the book! The tricks I picked up on rapidly moving around in the command line have saved me an enormous amount of time over the years.","sidebar":"sidebar"},"core-skills/index":{"id":"core-skills/index","title":"Part 2 - Core Skills","description":"In the first part of this book we look at the shell from the perspective of someone who is familiar with a graphical user interface. We studied how to transition from a GUI to the shell, introducing the \'shell way\' of performing tasks which you might have previously performed using a GUI.","sidebar":"sidebar"},"core-skills/job-control/index":{"id":"core-skills/job-control/index","title":"Job Control","description":"Job control is a feature of most shells which can often be somewhat complicated to work with. However, knowing the basics can help prevent you from getting yourself into a tangle and can from time to time make certain tasks a little easier.","sidebar":"sidebar"},"core-skills/thinking-in-pipelines/index":{"id":"core-skills/thinking-in-pipelines/index","title":"Thinking in Pipelines","description":"Understanding the concept of pipelines in the shell, as well as how input and output work for command line programs is critical to be able to use the shell effectively.","sidebar":"sidebar"},"core-skills/understanding-commands/index":{"id":"core-skills/understanding-commands/index","title":"Understanding Commands","description":"In this chapter, we\'ll take a look at the various different types of shell commands that exist and how this can affect your work. Commands are far more subtle than you might think and in this chapter we\'ll look at some of the nuances of commands and the practical consequences for your work.","sidebar":"sidebar"},"core-skills/what-is-a-shell/hack-on":{"id":"core-skills/what-is-a-shell/hack-on","title":"hack-on","description":"Hack On! See those system calls!"},"core-skills/what-is-a-shell/index":{"id":"core-skills/what-is-a-shell/index","title":"What is a Shell?","description":"This is the second of the \\"interludes\\" which end each section of the book. These interludes give flavour, concepts, context and the history of some of the concepts we\'re dealing with. These interlude are not essential to mastering the skills of the shell, but you might find them interesting.","sidebar":"sidebar"},"index":{"id":"index","title":"Effective Shell","description":"Investing just a few hours in your ability to use text based interfaces for your computer can have an enormous impact on your productivity. It can also make your work more fun, allowing you to maintain that creative \'flow\' state that can make technology so exciting.","sidebar":"sidebar"},"manipulating-text/advanced-text-manipulation/index":{"id":"manipulating-text/advanced-text-manipulation/index","title":"Advanced Text Manipulation","description":"In Chapter 15 we introduced some simple commands to work with text - specifically head, tail, tr and cut. Now we are going to introduce sed the Stream Editor command.","sidebar":"sidebar"},"manipulating-text/build-commands-on-the-fly/index":{"id":"manipulating-text/build-commands-on-the-fly/index","title":"Build Commands on the Fly","description":"In the earlier chapters of this part of the book we\'ve seen a number of ways to manipulate text. Now we\'re going to introduce the xargs command and show how to use our text manipulation skills to dynamically build complex commands on the fly.","sidebar":"sidebar"},"manipulating-text/get-to-grips-with-grep/index":{"id":"manipulating-text/get-to-grips-with-grep/index","title":"Get to Grips with Grep","description":"The grep tool is a real workhorse for shell users - once you\'ve learned how to use it you will find yourself using it again and again. In this chapter we\'ll see how you can use grep for common tasks, and how to use it in combination with other tools.","sidebar":"sidebar"},"manipulating-text/index":{"id":"manipulating-text/index","title":"Part 3 - Manipulating Text and Streams","description":"A key part of how Linux and Unix systems work is that almost everything is represented as a text file in the system. Almost everything can be configured with simple text file.","sidebar":"sidebar"},"manipulating-text/regex-essentials/index":{"id":"manipulating-text/regex-essentials/index","title":"Regex Essentials","description":"Many of the tools we\'re going to introduce in this part of the book support regular expressions or regexes - a sophisticated language which allows us to describe different patterns of text.","sidebar":"sidebar"},"manipulating-text/slice-and-dice-text/index":{"id":"manipulating-text/slice-and-dice-text/index","title":"Slice and Dice Text","description":"In Chapter 14 we looked at how to use the grep command to search through text and filter text. In this chapter we\'re going to look at some of the basic commands which we can use to manipulate text. There are a whole raft of commands and options available.","sidebar":"sidebar"},"shell-scripting/functions-parameters-and-error-handling/index":{"id":"shell-scripting/functions-parameters-and-error-handling/index","title":"Functions, Parameters and Error Handling","description":"The shell allows you to create functions - a set of commands that you can call at any time. In this chapter we\'ll see how to create functions and how function parameters and script parameters are handled. We will also look at status codes for commands and scripts and error handling.","sidebar":"sidebar"},"shell-scripting/index":{"id":"shell-scripting/index","title":"Part 4 - Shell Scripting","description":"Now that we\'ve looked at the core skills will help you be more effective, as well as the fundamentals of managing text, it is time to look at shell scripting.","sidebar":"sidebar"},"shell-scripting/loops-and-working-with-files-and-folders/index":{"id":"shell-scripting/loops-and-working-with-files-and-folders/index","title":"Loops and working with Files and Folders","description":"Loops allow us to perform a set of operations over multiple items, such as a set of files or folders or the results of a command. In this chapter we\'ll look at loops and how to operate on many files and folders.","sidebar":"sidebar"},"shell-scripting/mastering-conditional-logic/index":{"id":"shell-scripting/mastering-conditional-logic/index","title":"Mastering Conditional Logic","description":"In this chapter we\'ll introduce \'conditional logic\', a set of powerful features that allow us to run operations only when certain conditions are met. We\'ll look at the if statement and the different ways we can evaluate conditions. We\'ll also look at more sophisticated conditional constructs such as the case statement and the select statement, and how to \'chain\' commands based on conditions.","sidebar":"sidebar"},"shell-scripting/shell-script-essentials/index":{"id":"shell-scripting/shell-script-essentials/index","title":"Shell Script Essentials","description":"In this chapter we\'re going to look at how to write shell scripts and the different ways we can execute them. We\'ll look at how shell script files should be structured and how to use \'shebangs\' to define how a shell script will run.","sidebar":"sidebar"},"shell-scripting/useful-patterns-for-shell-scripts/index":{"id":"shell-scripting/useful-patterns-for-shell-scripts/index","title":"Useful Patterns for Shell Scripts","description":"To close this the section on shell scripting we\'re going to look at some common patterns you will see in shell scripts. These are an assortment of techniques you may find useful when building your scripts - you may come across them in scripts others have written as well.","sidebar":"sidebar"},"shell-scripting/variables-reading-input-and-mathematics/index":{"id":"shell-scripting/variables-reading-input-and-mathematics/index","title":"Variables, Reading Input, and Mathematics","description":"We\'ve seen variables a few times in our journey so far. In this chapter we\'ll look at variables in a bit more detail. We\'ll then see how to read input from the user and also look at how to perform basic mathematical operations in the shell.","sidebar":"sidebar"},"transitioning-to-the-shell/clipboard-gymnastics/index":{"id":"transitioning-to-the-shell/clipboard-gymnastics/index","title":"Becoming a Clipboard Gymnast","description":"For those who are new to the shell, we\'ve covered a lot. In this chapter we\'ll slow down the pace of new commands a bit and instead focus on a core skill which you will already be familiar with from Graphical User Interfaces - using the clipboard.","sidebar":"sidebar"},"transitioning-to-the-shell/getting-help/index":{"id":"transitioning-to-the-shell/getting-help/index","title":"Getting Help","description":"In the earlier chapters I\'ve introduced quite a few commands. Having to remember all of these commands and their parameters would be very hard. Fortunately there are built-in capabilities in the shell to help.","sidebar":"sidebar"},"transitioning-to-the-shell/getting-started/index":{"id":"transitioning-to-the-shell/getting-started/index","title":"Getting Started","description":"This section is for those who are completely new to this topic. In this section we\'ll introduce just what the shell is, who this book is useful for, and what you can expect to learn.","sidebar":"sidebar"},"transitioning-to-the-shell/index":{"id":"transitioning-to-the-shell/index","title":"Part 1 - Transitioning to the Shell","description":"These are the key skills which everyone should know. Without them, you might struggle to perform certain tasks at all. Experienced users can probably skip this section, or just review the summary. But if you are new to the shell, this is the best place to start! This section focuses on helping you quickly get up to speed with how to perform the same kind of tasks you might have performed in a GUI (Graphical User Interface) with the shell.","sidebar":"sidebar"},"transitioning-to-the-shell/managing-your-files/index":{"id":"transitioning-to-the-shell/managing-your-files/index","title":"Managing Your Files","description":"Downloading, unzipping, copying, moving, renaming and deleting files in a graphical user interface is normally fairly intuitive. Now we\'ll learn how to perform the same operations in a shell. Once you can organise your files, you are well on your way to being able to use the shell more effectively for day to day tasks.","sidebar":"sidebar"},"transitioning-to-the-shell/navigating-your-system/index":{"id":"transitioning-to-the-shell/navigating-your-system/index","title":"Navigating Your System","description":"Switching from a graphical user interface to the shell can take some getting used to. First we\'ll take a look at how to navigate your system using the shell, and get information on files and folders in the system.","sidebar":"sidebar"},"transitioning-to-the-shell/the-renaissance-of-the-shell/index":{"id":"transitioning-to-the-shell/the-renaissance-of-the-shell/index","title":"The Renaissance of the Shell","description":"This is the first of the \\"interludes\\" which end each section of the book. They don\'t teach any specific skills but instead give a little flavour and background about the world of the shell, Linux and modern computing.","sidebar":"sidebar"},"work-in-progress":{"id":"work-in-progress","title":"Work in Progress!","description":"If you have landed here, then most likely you have clicked a link to a chapter which has not yet been completed. Sorry about that!"},"xx-appendices/essential-manpages":{"id":"xx-appendices/essential-manpages","title":"essential-manpages","description":"The Most Important Manpages"},"xx-appendices/exercises":{"id":"xx-appendices/exercises","title":"Good Scripts to write as exercises","description":"- recent - a better version of history, which deduplicates and sorts based on the most commonly used items"},"xx-appendices/index-of-commands":{"id":"xx-appendices/index-of-commands","title":"index-of-commands","description":"| Command                         | Description                                                                                 |"},"xx-appendices/installing-samples/index":{"id":"xx-appendices/installing-samples/index","title":"Installing the Samples","description":"How to install the Effective Shell Samples","sidebar":"sidebar"},"xx-appendices/posix":{"id":"xx-appendices/posix","title":"Posix","description":"Posix Shells"},"xx-appendices/recommended-reading/index":{"id":"xx-appendices/recommended-reading/index","title":"Recommended Reading","description":"Great books and articles you might enjoy if you like this book!","sidebar":"sidebar"},"xx-appendices/shell-parameter-expansion":{"id":"xx-appendices/shell-parameter-expansion","title":"shell-parameter-expansion","description":"| Variable           | Description                                                                                   |"},"xx-appendices/thanks":{"id":"xx-appendices/thanks","title":"Thanks","description":"Many people have contributed their time and support to help build this book. Thank-you to everyone who has helped on this journey!","sidebar":"sidebar"},"xx-appendices/the-future":{"id":"xx-appendices/the-future","title":"the-future","description":"The shell, in particular the Bourne-Again Shell has been popular for many years. But what does the future hold? With the advent of the Linux Subsystem for Windows, new shells like Nushell, and the latest version of MacOS switching from Bash to Z-Shell, we finish off by looking at some of the trends which might shape how we use shells in the future."},"zz-developer-guide/components":{"id":"zz-developer-guide/components","title":"Components","description":"This page shows the custom components that are available for Effective Shell. These are primarily used to improve the reading experience for code samples.","sidebar":"sidebar"},"zz-developer-guide/images-and-diagrams":{"id":"zz-developer-guide/images-and-diagrams","title":"Images and Diagrams","description":"Images","sidebar":"sidebar"},"zz-developer-guide/markdown-guide":{"id":"zz-developer-guide/markdown-guide","title":"Markdown","description":"The contents of this book are primarily written in MDX. Some key concepts to be aware of:","sidebar":"sidebar"},"zz-developer-guide/recording-terminal-sessions":{"id":"zz-developer-guide/recording-terminal-sessions","title":"Recording Terminal Sessions","description":"There are a couple of techniques that can be useful to record terminal sessions. The first is the asciinema too. The second is the script and scriptreplay commands, which can be used to record the actual keystrokes typed and then replay them.","sidebar":"sidebar"}}}')}}]);